mapper.select:
UPDATE user SET
	, TYPE = #{type}
 	, NAME = #{name}
    , SEX = #{sex}
  	, BIRTH = #{birth}
  	, PHONE = #{phone}
  	, ZIPCODE = #{zipcode}
  	, ADDRESS = #{address}
WHERE SEQ = #{seq}

INSERTO INTO 
	(
	   SEQ
	 , TYPE
	 , NAME
	 , SEX
	 , BIRTH
	 , PHONE
	 , ZIPCODE
	 , ADDRESS
	 , REG_DATE
	 , CNF_YN
	 , LEAVE_YN
	) 
VALUES
	(
   	   ${seq}
	 , ${type}
	 , ${name}
	 , ${sex}
	 , ${birth}
	 , ${phone}
	 , ${zipcode}
	 , ${address}
	 , ${regDate}
	 , ${cnfYn}
	 , ${leaveYn}
	)
